var verifyCode = new GVerify("v_container");
document.getElementById("subscribe").onclick = function() {
	var res = verifyCode
			.validate(document.getElementsByClassName("code_input")[0].value);
	if (res) {
		alert("验证正确")
	} else {
		alert("验证码错误");
		document.getElementsByClassName("code_input")[0].value = ''
	}
};